(() => {
  "use strict";
  document.addEventListener("DOMContentLoaded", function () {
    document.addEventListener("mousemove", function (t) {
      this.querySelectorAll(".animatedElement").forEach(function (e) {
        var n = e.getAttribute("data-speed"),
          a = e.getAttribute("data-direction"),
          o = (window.innerWidth - t.pageX * n) / 100,
          r = (window.innerHeight - t.pageY * n) / 100;
        "both" === a
          ? (e.style.transform = "translateX("
              .concat(o, "px) translateY(")
              .concat(r, "px)"))
          : "moveX" === a
          ? (e.style.transform = "translateX(".concat(o, "px)"))
          : "moveY" === a &&
            (e.style.transform = "translateY(".concat(r, "px)"));
      });
    });
  });
})();
